DROP TABLE IF EXISTS public.livros;

CREATE TABLE IF NOT EXISTS public.livros
(
    "ID" SERIAL NOT NULL,
    "Titulo" character varying(255) COLLATE pg_catalog."default" NOT NULL,
    "Codigo" character varying(13)  COLLATE pg_catalog."default" NOT NULL,
    "Descricao" text COLLATE pg_catalog."default",
    "Editora" character varying(255) COLLATE pg_catalog."default" NOT NULL,
    "Numero de paginas" smallint NOT NULL,
    "Preco" numeric(12,2) NOT NULL,
    "Formato" character varying(255) COLLATE pg_catalog."default" NOT NULL,
    "Quantidade" integer NOT NULL,
    "Categoria" character varying(255) COLLATE pg_catalog."default",
    CONSTRAINT "Livro_pkey" PRIMARY KEY ("ID", "Codigo")
)

TABLESPACE pg_default;

